tinymce.PluginManager.add('jbimages', function (editor, url) {
    function jbBox() {
        editor.windowManager.open({title: 'Загрузить файл', file: url + '/dialog.php', width: 350, height: 235,})
    }

    editor.addButton('jbimages', {tooltip: 'Загрузить файл', icon: 'image', text: 'Загрузить', onclick: jbBox});
    editor.addMenuItem('jbimages', {text: 'Upload image', icon: 'image', context: 'insert', onclick: jbBox})
});